<script>

import _ from 'lodash';

export default {
    name: "PreviewCard",
    props: {
        name: String,
        icon: String,
        mods: Array,
        selectedMods: Array,
        overclock: Object
    },
    methods: {
        getModTooltip(mod) {
            let description = mod.description ? mod.description : mod.text_description;
            return `<h3>${mod.mod_name}</h3><br><span>${description}</span>`;
        },
        getOverclockTooltip(oc) {
            if (!oc) {
                return null;
            }
            return `<h3>${oc.overclock_name}</h3><br><span>${oc.text_description}</span>`;
        },
        isActiveMod(mod) {
            return this.selectedMods.includes(mod.id);
        },
        sortMods(mods) {
            return _.sortBy(mods, 'mod_index');
        }
    },
    computed: {
        modMatrix() {
            return _.groupBy(this.mods, 'mod_tier');
        },
    }
}
</script>

